files.iac/terraform/{{moduleNameKebab}}/main.t:
terraform {
  required_version = ">= 1.7.0"
}

provider \"aws\" {
  region = var.region
}

module \"tags\" {
  source = \"../../modules/tags\"
  environment = var.environment
}

files.iac/terraform/{{moduleNameKebab}}/variab:
variable \"environment\" {
  type = string
}

variable \"region\" {
  type    = string
  default = \"ap-northeast-1\"
}

files.iac/terraform/{{moduleNameKebab}}/backen:
terraform {
  backend \"s3\" {
    bucket         = \"{{stateBucket}}\"
    key            = \"{{stateKeyPrefix}}{{moduleNameKebab}}/terraform.tfstate"
    region         = \"{{stateRegion}}\"
    dynamodb_table = \"{{stateDynamoTable}}\"
  }
}
