files.iac/terraform/{{moduleNameKebab}}/variab:
variable "environment" {
  type = string
}

variable "region" {
  type    = string
  default = "ap-northeast-1"
}

files.iac/terraform/{{moduleNameKebab}}/backen:
terraform {
  backend "s3" {
    bucket = "itdo-terraform-state"
    key    = "{{moduleNameKebab}}/terraform.tfstate"
    region = "ap-northeast-1"
    dynamodb_table = "itdo-terraform-locks"
  }
}
